import React, { useState } from 'react';
import './App.css';

function App() {
  const [stocks, setStocks] = useState([
    { sector: '', holdingLength: '', maxPrice: '' },
  ]);
  const [recommendations, setRecommendations] = useState('');
  const [hasClickedRecommend, setHasClickedRecommend] = useState(false);

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:3001/get-recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ stocks: stocks })
      });

      console.log('Response:', response);

      if (response.ok) {
        const data = await response.json();
        setRecommendations(data.recommendations);
        setHasClickedRecommend(true);
      } else {
        console.error('Failed to retrieve recommendations');
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>Hello AISC!</p>
        <form onSubmit={e => e.preventDefault()}>
          {stocks.map((stock, index) => (
            <div key={index}>
              <input 
                placeholder="Sector of Stock" 
                value={stock.sector}
                onChange={e => handleInputChange(index, 'sector', e.target.value)}
              />
              <input 
                placeholder="Holding Length" 
                value={stock.holdingLength}
                onChange={e => handleInputChange(index, 'holdingLength', e.target.value)}
              />
              <input 
                placeholder="Max Price" 
                value={stock.maxPrice}
                onChange={e => handleInputChange(index, 'maxPrice', e.target.value)}
              />
            </div>
          ))}
          <button onClick={handleSubmit}>Get Stock Recommendations</button>
        </form>

        {hasClickedRecommend && (
          <div>
            <h2>Based on your preferences, here are some stock recommendations:</h2>
            <p>{recommendations}</p>
          </div>
        )}
      </header>
    </div>
  );

  function handleInputChange(index, field, value) {
    const newStocks = [...stocks];
    newStocks[index][field] = value;
    setStocks(newStocks);
  }
}

export default App;
