import React, { useState } from 'react';
import './App.css';

function App() {
  const [movies, setMovies] = useState([
    { title: '', rating: 0 },
    { title: '', rating: 0 },
    { rating: 1} //default slider value
  ]);
  const [recommendations, setRecommendations] = useState('');
  const [hasClickedRecommend, setHasClickedRecommend] = useState(false);

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:3001/get-recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        // You can send movie data to your backend if needed
        // body: JSON.stringify({ movies: movies })
        body: JSON.stringify({ movies: movies })
      });

      if (response.ok) {
        const data = await response.json();
        setRecommendations(data.recommendations);
        setHasClickedRecommend(true);
      } else {
        console.error('Failed to retrieve recommendations');
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>Hello!</p>
        <form onSubmit={e => e.preventDefault()}>
          {movies.map((movie, index) => (
            <div key={index}>
            {index === 0 ? (
              <div>
                <p>Sector</p>
                <input
                  placeholder={`Enter Sector`}
                  value={movie.title}
                  onChange={(e) => handleInputChange(index, 'title', e.target.value)}
                />
              </div>
            ) : index === 1 ? (
              <div>
                <p>Length</p>
                <input
                  placeholder={`Enter Length`}
                  value={movie.title}
                  onChange={(e) => handleInputChange(index, 'title', e.target.value)}
                />
              </div>
            ) : (
              <div>
                <p>Max Price</p>
                <input
                  type="range"
                  min={1}
                  max={1000}
                  value={movie.rating}
                  onChange={(e) => handleInputChange(index, 'rating', parseInt(e.target.value))}
                />
              </div>
            )}
          </div>
          ))}
          <button onClick={handleSubmit}>Get Recommendations</button>
        </form>

        {hasClickedRecommend && (
          <div>
            <h2>Based on your preferences, here are some more movie recommendations:</h2>
            <p>{recommendations}</p>
          </div>
        )}
      </header>
    </div>
  );

  function handleInputChange(index, field, value) {
    const newMovies = [...movies];
    newMovies[index][field] = value;
    setMovies(newMovies);
  }

  // function handleStarClick(index, starNumber) {
  //   handleInputChange(index, 'rating', starNumber);
  // }
}

export default App;
