import React, { useState } from 'react';
import './App.css';

function App() {
  const [movies, setMovies] = useState([
    { title: ''},
    {},
    {}
  ]);
  const [recommendations, setRecommendations] = useState('');
  const [hasClickedRecommend, setHasClickedRecommend] = useState(false);

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:3001/get-recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        // You can send movie data to your backend if needed
        // body: JSON.stringify({ movies: movies })
        body: JSON.stringify({ movies: movies })
      });

      if (response.ok) {
        const data = await response.json();
        setRecommendations(data.recommendations);
        setHasClickedRecommend(true);
      } else {
        console.error('Failed to retrieve recommendations');
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>Generative AI Stock Advisor</p>
        <form onSubmit={e => e.preventDefault()}>
          {movies.map((movie, index) => (
            <div key={index}>
                value={movie.title}
                <div>
                <p>Stock Market Sector</p>
                <select
                    value={movie.title}
                    onChange={(e) => handleInputChange(index, 'sector', e.target.value)}
                  >
                    <option value="healthcare">Choose One</option>
                    <option value="healthcare">Healthcare</option>
                    <option value="materials">Materials</option>
                    <option value="real estate">Real Estate</option>
                    <option value="consumer staples">Consumer Staples</option>
                    <option value="consumer discretionary">Consumer Discretionary</option>
                    <option value="utilities">Utilities</option>
                    <option value="energy">Energy</option>
                    <option value="industrials">Industrials</option>
                    <option value="consumer services">Consumer Services</option>
                    <option value="financials">Financials</option>
                    <option value="technology">Technology</option>
                  </select>
              </div>

                onChange={e => handleInputChange(index, 'title', e.target.value)}
            </div>
          ))}
          <button onClick={handleSubmit}>Get Recommendations</button>
        </form>

        {hasClickedRecommend && (
          <div>
            <h2>Based on your preferences, here are some more stock recommendations:</h2>
            <h5>{recommendations}</h5>
          </div>
        )}
      </header>
    </div>
  );

  function handleInputChange(index, field, value) {
    const newMovies = [...movies];
    newMovies[index][field] = value;
    setMovies(newMovies);
  }
}

export default App;
